def get_scheme_data(schemeName):
    if schemeName == "Pradhan Mantri Kisan Samman Nidhi":
        return dict[1]
    elif schemeName == "Pradhan Mantri Kisan Maan Dhan Yojana":
        return dict[2]
    elif schemeName == "Pradhan Mantri Fasal Bima Yojana":
        return dict[3]

